import './App.css';
import React, {useState, useReducer} from 'react';

function App() {
  const [checked, setChecked] = useState(false);

  return(
    <>
     <input type = "checkbox" value = "checked" onChange = {((checked) => !checked)} />
     {<p>{checked ? "checked" : "not checked"}</p>}
    </>

  )
}



export default App;
