import './App.css';
import React, {useState} from 'react';

function App() {
  const [emotion, setEmotion] = useState("Happy");
  return(
    <>
      <h1>I am feeling {emotion}</h1>
      <button onClick = {() => setEmotion("frustrated")}>Frustrated</button>
      <button onClick = {() => setEmotion("excited")}>Excited</button>



    </>

  )
}



export default App;
